css:
 [data-panda-theme=light] {
    --font-sizes-xs: 0.75rem;
    --font-sizes-sm: 0.875rem;
    --font-sizes-md: 1rem;
    --font-sizes-lg: 1.125rem;
    --font-sizes-xl: 1.25rem;
    --font-weights-normal: 400;
    --font-weights-medium: 500;
    --font-weights-bold: 700;
    --space-1: 0.25rem;
    --space-2: 0.5rem;
    --space-3: 0.75rem;
    --space-4: 1rem;
    --space-5: 1.25rem;
    --space-6: 1.5rem;
    --colors-background: var(--colors-white);
    --colors-text: var(--colors-gray-900);
    --colors-primary: var(--colors-blue-500);
    --colors-secondary: var(--colors-blue-500);
    --colors-button-background: var(--colors-sky-800);
    --colors-button-background-hover: var(--colors-sky-600);
    --colors-button-text: var(--colors-sky-50);
    --font-sizes-body: var(--font-sizes-md);
    --font-sizes-heading: var(--font-sizes-xl);
    --font-weights-body: var(--font-weights-normal);
    --font-weights-heading: var(--font-weights-bold);
    --space-small: var(--space-2);
    --space-medium: var(--space-4);
    --space-large: var(--space-6)
}
